
public class Student {

	String name;
	int age;
	public Student(String string, int i) {
		// TODO Auto-generated constructor stub
		this.name = string;
		this.age = i;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getAge() {
		return age;
	}
	public void setAge(int age) {
		this.age = age;
	}
	
	
}
